tool.command:
## a temp file is needed, because the standard tabular output from infernal is not usefull in Galaxy ## it will be converted to a tab delimited file and piped to Galaxy temp_tabular_output=\$(mktemp);
cmstat
#if str($cm_opts.cm_opts_selector) == "db": $cm_opts.database.fields.path #else: $cm_opts.cmfile #end if
> \$temp_tabular_output ;
## 1. replace all lines starting # (comment lines) ## 2. replace the first 18 spaces with tabs, 18th field is a free text field (can contain spaces) sed -e 's/#.*$//' -e '/^$/d' -e 's/ /\t/g' -e 's/\t/ /18g' \$temp_tabular_output > $outfile

tool.citation:
@ARTICLE{bgruening_galaxytools,
    Author = {Björn Grüning, Cameron Smith, Torsten Houwaart, Nicola Soranzo, Eric Rasche},
    keywords = {bioinformatics, ngs, galaxy, cheminformatics, rna},
    title = {{Galaxy Tools - A collection of bioinformatics and cheminformatics tools for the Galaxy environment}},
    url = {https://github.com/bgruening/galaxytools}
}
